let map = null;
let geocoder = null;
let positionWatcher;

function initialize() {
  if (GBrowserIsCompatible()) {
    map = new GMap2(document.getElementById('map_canvas'));
    map.setCenter(new GLatLng(37.4419, -122.1419), 1);
    map.setUIToDefault();
    geocoder = new GClientGeocoder();

    document.querySelector('button').addEventListener('click', e => {
      if (e.currentTarget.dataset.watch === 'true') {
        const address = document.querySelector('#destination').value;
        startWatchingAddress(address);
        e.preventDefault();
        e.currentTarget.dataset.watch = 'false';
        e.currentTarget.innerHTML = 'Ok stop beeping already!!';
      } else {
        navigator.geolocation.clearWatch(positionWatcher);
        e.currentTarget.dataset.watch = 'true';
        e.currentTarget.innerHTML = `I want to go back to sleep - let me know when I'm close`;
      }
    });
  }
}

initialize();

function startWatchingAddress(address) {
  if (geocoder) {
    geocoder.getLatLng(address, function(point) {
      if (!point) {
        alert(address + ' not found');
      } else {
        map.setCenter(point, 15);
        var marker = new GMarker(point, {draggable: true});
        map.addOverlay(marker);
        GEvent.addListener(marker, 'dragend', function() {
          marker.openInfoWindowHtml(marker.getLatLng().toUrlValue(6));
        });
        GEvent.addListener(marker, 'click', function() {
          marker.openInfoWindowHtml(marker.getLatLng().toUrlValue(6));
        });
        GEvent.trigger(marker, 'click');

        letMeKnowWhenImClose({
          latitude: point.lat(),
          longitude: point.lng(),
        });
      }
    });
  }
}

function letMeKnowWhenImClose(target) {
  function success(pos) {
    const currentLocation = pos.coords;
    const distance = haversineDistance(target, currentLocation);
    const resultsDiv = document.querySelector('#results');
    resultsDiv.innerHTML = `You are now ${distance.toFixed(2)} km away`;
    if (distance < 1.5) {
      beep();
      beep();
    } else if (distance < 0.25) {
      beep();
      beep();
      beep();
      beep();
    }
  }

  function error(err) {
    console.warn('ERROR(' + err.code + '): ' + err.message);
  }

  const options = {
    enableHighAccuracy: false,
    timeout: 5000,
    maximumAge: 0,
  };

  positionWatcher = navigator.geolocation.watchPosition(
    success,
    error,
    options
  );
}

function haversineDistance(destination, currentLocation) {
  const toRad = x => {
    return x * Math.PI / 180;
  };

  var lon1 = currentLocation.longitude;
  var lat1 = currentLocation.latitude;

  var lon2 = destination.longitude;
  var lat2 = destination.latitude;

  var radiusOfEarth = 6371; // radius of km

  var x1 = lat2 - lat1;
  var dLat = toRad(x1);
  var x2 = lon2 - lon1;
  var dLon = toRad(x2);
  var a =
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(toRad(lat1)) *
      Math.cos(toRad(lat2)) *
      Math.sin(dLon / 2) *
      Math.sin(dLon / 2);
  var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  var d = radiusOfEarth * c;

  return d;
}

function beep() {
  var snd = new Audio(
    'data:audio/wav;base64,'
  );
  snd.play();
}
